css:
.subtitle {
    font-size: 14px;
    font-weight: 600; /* Medium weight */
    margin-top: 0px;
}

.value {
    margin-left: 10px; /* Slight indentation for better readability */
}
.section-title {
    font-size: 16px;
    font-weight: bold;
    color: #0056b3; /* Blue title */
    margin-bottom: 0; /* Remove bottom margin */
    padding-bottom: 0; /* Ensure no extra spacing */
}

.table {
    margin-top: 0; /* Remove any top margin */
    border-collapse: collapse; /* Ensure no extra spacing */
    width: 100%;
}
.inline-fields {
    margin-top: 05px;
    display: flex;
    gap: 150px; /* Space between Incoterm and Named Place */
}

.inline-fields p {
    
    margin: 0; /* Remove extra margins */
    font-weight: 300; /* Slightly bold */
}

.large-terms {
    font-size: 14px;
    font-weight: 600;
    margin-top: 05px;
}
.medium-text {
    font-weight: 500; /* Medium-bold */
}

@page {
        size: A4;
        margin: 15mm;
    }
    
    body {
        font-family: Arial, sans-serif;
        direction: rtl;
        text-align: right;
    /*font-family: "Arial", sans-serif;*/
        font-size: 12px;
        line-height: 1.4;
        margin: 0;
        padding: 0;
    }
   

    .header {
        display: flex;
        justify-content: space-between;
        align-items: flex-start;
        margin-bottom: 20px;
        /*border-bottom: 2px solid #000;*/
        padding-bottom: 10px;
    }
    
    .header-left {
        flex: 1;
    }
    
    .header-right {
        flex: 1;
        text-align: right;
    }
    
    .kingdom {
        font-weight: bold;
        font-size: 14px;
        margin-bottom: 5px;
    }
    
    .organization {
        font-weight: bold;
        font-size: 13px;
        margin-bottom: 3px;
    }
    
    .department {
        font-weight: bold;
        font-size: 12px;
    }
    
    .original-note {
        font-size: 11px;
        font-weight: bold;
        margin-bottom: 15px;
    }
    
    .document-details {
        display: flex;
        justify-content: space-between;
        margin-bottom: 20px;
        /*background-color: #f8f9fa;*/
        padding: 10px;
        /*border: 1px solid #ddd;*/
    }
    
    .detail-group {
        display: flex;
        flex-direction: column;
        gap: 8px;
    }
    
    .detail-row {
        display: flex;
        align-items: center;
        gap: 10px;
    }
    
    .detail-label {
        font-weight: bold;
        min-width: 100px;
    }
    
    .detail-input {
        /*border-bottom: 1px solid #000;*/
        min-width: 150px;
        height: 20px;
        display: inline-block;
    }
    
    .document-title {
        text-align: center;
        font-size: 16px;
        font-weight: bold;
        margin: 20px 0;
        padding: 10px;
        background-color: #e9ecef;
        border: 2px solid #000;
    }
    
    .return-reasons {
        margin-bottom: 20px;
        padding: 15px;
        border: 1px solid #000;
    }
    
    .reasons-title {
        font-weight: bold;
        text-align: center;
        margin-bottom: 10px;
        font-size: 14px;
    }
    
    .reasons-grid {
        display: grid;
        grid-template-columns: repeat(4, 1fr);
        gap: 15px;
    }
    
    .reason-item {
        display: flex;
        align-items: center;
        gap: 8px;
    }
    
    .checkbox {
        width: 15px;
        height: 15px;
        border: 2px solid #000;
        display: inline-block;
    }
    
    .items-table {
        width: 100%;
        border-collapse: collapse;
        margin-bottom: 30px;
        font-size: 11px;
    }
    
    .items-table th,
    .items-table td {
        border: 1px solid #000;
        padding: 8px;
        text-align: center;
        vertical-align: middle;
    }
    
    .items-table th {
        background-color: #f8f9fa;
        font-weight: bold;
        font-size: 10px;
    }
    
    .items-table .description-col {
        width: 25%;
        text-align: left;
    }
    
    .items-table .recommendations-col {
        width: 35%;
    }
    
    .recommendation-options {
        display: flex;
        flex-direction: column;
        gap: 5px;
        font-size: 9px;
    }
    
    .recommendation-item {
        display: flex;
        align-items: center;
        gap: 5px;
    }
    
    .small-checkbox {
        width: 10px;
        height: 10px;
        border: 1px solid #000;
        display: inline-block;
    }
    
    .signatures-section {
        margin-top: 40px;
    }
    
    .signatures-table {
        width: 100%;
        border: 1px solid #000;
        border-collapse: collapse;
        margin-bottom: 20px;
    }
    
    .signatures-table th{
        border: 1px solid #000;
        padding: 15px;
        text-align: center;
        vertical-align: top;
    
    }
    .signatures-table td {
        border: 1px solid #000;
        padding: 15px;
        text-align: center;
        vertical-align: top;
    }
    
    .signatures-table th {
        background-color: #f8f9fa;
        font-weight: bold;
        font-size: 11px;
    }
    
    .signature-cell {
        height: 80px;
    }
    
    .signature-labels {
        display: flex;
        flex-direction: column;
        gap: 25px;
        text-align: left;
    }
    
    .authorization {
        margin-top: 20px;
        display: flex;
        flex-direction: column;
        gap: 15px;
    }
    
    .auth-row {
        display: flex;
        align-items: center;
        gap: 10px;
    }
    
    .auth-label {
        font-weight: bold;
        min-width: 120px;
    }
    
    .auth-line {
        border-bottom: 1px solid #000;
        flex: 1;
        height: 20px;
    }
    
    .page-break {
        page-break-before: always;
    }

