{% set days_map = {"Daily": 1, "Weekly": 7} %}
{% set months_map = {
    "Monthly": 1, "Quarterly": 3, "Half-yearly": 6,
    "Yearly": 12, "2 Yearly": 24, "3 Yearly": 36
} %}

<style>
  h2 { text-align: center; margin: 0 0 4mm; }
  table { width: 100%; border-collapse: collapse; font-size: 9pt; }
  th, td { border: 0.1mm solid #777; padding: 2mm 3mm; }
  th { background: #f5f5f5; }
</style>

<h2>Preventive Maintenance Schedule<br>
  <small>{{ doc.name }}</small>
</h2>

<h3>Asset&nbsp;Details</h3>

<table>
  <thead>
    <tr>
      <th style="width:4mm;">#</th>
      <th>Asset ID</th>
      <th>Asset Name</th>
      <th>Serial Number</th>
      <th>Manufacturer</th>
      <th>Model</th>
    </tr>
  </thead>
  <tbody>

    {% if doc.maintenance_entries %}
      {% for m in doc.maintenance_entries %}
        {% set asset_doc = frappe.get_doc("Asset", m.asset) %}
        <tr>
          <td>{{ loop.index }}</td>
          <td>{{ m.asset }}</td>                       
          <td>{{ m.asset_name }}</td>                  
          <td>{{asset_doc.custom_serial_number or "" }}</td>
          <td>{{asset_doc.custom_manufacturer or "" }}</td>
          <td>{{asset_doc.custom_model or "" }}</td>
         
        </tr>
      {% endfor %}
    {% else %}
      <tr><td colspan="6">No asset rows in maintenance_entries.</td></tr>
    {% endif %}

  </tbody>
</table>
<br>
<h3>PM&nbsp;Schedule</h3>
<table>
  <thead>
    <tr>
      <th style="width:4mm;">#</th>
      <th>Due Date</th>
      <th>Periodicity</th>
      <th>Assigned To</th>
      <th>Maintenance Type</th>
      <th>Status</th>
    </tr>
  </thead>
  <tbody>

    {% if doc.start_date and doc.end_date and doc.periodicity %}
      {% set sd = doc.start_date %}
      {% set ed = doc.end_date %}

      {# ---------------- day‑based periodicities ---------------- #}
      {% if doc.periodicity in days_map %}
        {% set step = days_map[doc.periodicity] %}
        {# start at the FIRST interval, i.e. skip n = 0 #}
        {% for n in range(step, frappe.utils.date_diff(ed, sd)+1, step) %}
          <tr>
            <td>{{ loop.index }}</td>
            <td>{{ frappe.format(frappe.utils.add_days(sd, n), {'fieldtype': 'Date'}) }}</td>
            <td>{{ doc.periodicity }}</td>
            <td>{{ doc.assign_to or "" }}</td>
            <td>PM</td>
            <td>Planned</td>
          </tr>
        {% endfor %}

      {# -------------- month‑based periodicities ---------------- #}
      {% elif doc.periodicity in months_map %}
        {% set step = months_map[doc.periodicity] %}
        {% set month_diff = frappe.utils.month_diff(ed, sd) %}
        {% set occ = (month_diff // step) + 1 %}
        {# start at i = 1 to skip the start date itself #}
        {% for i in range(1, occ) %}
          <tr>
            <td>{{ loop.index }}</td>
            <td>{{ frappe.format(frappe.utils.add_months(sd, i * step), {'fieldtype': 'Date'}) }}</td>
            <td>{{ doc.periodicity }}</td>
            <td>{{ doc.assign_to or "" }}</td>
            <td>PM</td>
            <td>Planned</td>
          </tr>
        {% endfor %}

      {% else %}
        <tr><td colspan="4">Unknown periodicity: {{ doc.periodicity }}</td></tr>
      {% endif %}
    {% else %}
      <tr><td colspan="4">Start Date, End Date, or Periodicity is missing.</td></tr>
    {% endif %}

  </tbody>
</table>

